mapper.select:
select j.`order_date`, j.`bed_count`, k.`lease_count`, k.`rent_count` from ( select month(create_time) as `order_date`, count(*) as `bed_count` from tb_jp_bed
and year(create_time) = #{year}
group by `order_date` ) j left join ( select month(pay_time) as `order_date`, count(*) as `lease_count`, sum(totalpay) as `rent_count` from tb_jp_orders where orders_status = '已完成'
and year(pay_time) = #{year}
group by `order_date` ) k on j.`order_date` = k.`order_date`
order by j.`order_date` desc

select j.`order_date`, j.`bed_count`, k.`lease_count`, k.`rent_count`
from (
    select day(create_time) as `order_date`,
    count(*) as `bed_count`
    from tb_jp_bed


            and year(create_time) = #{year}


            and month(create_time) = #{month}


    group by `order_date`
) j left join (
    select day(pay_time) as `order_date`,
    count(*) as `lease_count`,
    sum(totalpay) as `rent_count`
    from tb_jp_orders
    where orders_status = '已完成'

        and year(pay_time) = #{year}


        and month(pay_time) = #{month}

    group by `order_date`
) k on j.`order_date` = k.`order_date`
order by j.`order_date` desc
